import weka.classifiers.trees.J48;
import weka.core.DenseInstance;
import weka.core.Instance;
import weka.core.Instances;
import weka.core.converters.ConverterUtils.DataSource;

public class provaDeConceitoIris {

	public static void main(String[] args) throws Exception {
		// abre a base de dados no formato arff
		DataSource ds = new DataSource("src/brlaw/tech/data/iris.arff");
		Instances trainingSet = ds.getDataSet();
		
		// ler o dataset de treiino
		// System.out.println(trainingSet.toString());
		
		// diz que a classe é o quarto índice do arquivo que foi carregado
		trainingSet.setClassIndex(4);
		
		// vamos usar um algoritmo de árvore, o J48
		J48 classificador = new J48();
		classificador.buildClassifier(trainingSet);
		
		//opções de saída
		// 1. ver gráfico em forma de regra
		String resultado = classificador.graph();
		System.out.println(resultado);
		
		// ver gráfico em forma de desenho
		String resultado2 = classificador.toString();
		System.out.println(resultado2);
		
		//cria a instância de teste
		DenseInstance novoCaso = new DenseInstance(5);
		novoCaso.setDataset(trainingSet); //relaciona o training set com o testSet
		
		//adiciona valores ao novo caso 7,3.2,4.7,1.4,Iris-versicolor
		/*
		 *  1. sepal length in cm
  			2. sepal width in cm
  		    3. petal length in cm
 			4. petal width in cm
		 */
	//	novoCaso.setValue(0,2);
	//	novoCaso.setValue(1,3.2);
	//	novoCaso.setValue(2,4.7);
	//	novoCaso.setValue(3,1.4);
		
		//emula um setosa 1
		//novoCaso.setValue(0,2);
		//novoCaso.setValue(1,3.2);
		//novoCaso.setValue(2,4.7);
		//novoCaso.setValue(3,0.2);
		
		//emula um versicolor
		novoCaso.setValue(0,2);
		novoCaso.setValue(1,3.2);
		novoCaso.setValue(2,4.7);
		novoCaso.setValue(3,0.7);
		
		//emula um virginica
		//novoCaso.setValue(0,2); //sepal length
		//novoCaso.setValue(1,3.2); // sepal width
		//novoCaso.setValue(2,4.95); // petal length
		//novoCaso.setValue(3,1.5); //petal width
		

		
		//ver quais atributos são possíveis
		System.out.println("classes possíveis: " + novoCaso.classAttribute());

	    //classifica a instância	
		double classificacao = classificador.classifyInstance(novoCaso);	
		System.out.println(novoCaso.classAttribute().value((int) classificacao));
		
		double probabilidade[] = classificador.distributionForInstance(novoCaso);
		System.out.println("Probabilidade de ser Iris-Setosa : " +probabilidade[0]);
		System.out.println("Probabilidade de ser Iris Versicolor : " +probabilidade[1]);
		System.out.println("Probabilidade de ser Iris Virginica : " +probabilidade[2] );
	}

}
